import {
  FOLLOW_USER,
  GET_USER,
  UNFOLLOW_USER,
  UPDATE_BIO,
  UPLOAD_PICTURE,
} from "../actions/user.actions";

const initialState = {};

export default function userReducer(state = initialState, {type,payload}) {
  switch (type) {
    case GET_USER:
      return payload;
    case UPLOAD_PICTURE:
      return {
        ...state,
        picture: payload,
      };
    case UPDATE_BIO:
      return {
        ...state,
        bio: payload,
      };
    case FOLLOW_USER:
      return {
        ...state,
        following: [payload.idToFollow, ...state.following],
      };
    case UNFOLLOW_USER:
      return {
        ...state,
        following: state.following.filter(
          (id) => id !== payload.idToUnfollow
        ),
      };
    default:
      return state;
  }
}
