import { Observable, Subject } from 'rxjs';

export class StateObservable<S> extends Observable<S> {
  value: S;
  private __notifier = new Subject<S>();

  constructor(input$: Observable<S>, initialState: S) {
    super((subscriber) => {
      const subscription = this.__notifier.subscribe(subscriber);
      if (subscription && !subscription.closed) {
        subscriber.next(this.value);
      }
      return subscription;
    });

    this.value = initialState;
    input$.subscribe((value) => {
      // We only want to update state$ if it has actually changed since
      // redux requires reducers use immutability patterns.
      // This is basically what distinctUntilChanged() does but it's so simple
      // we don't need to pull that code in
      if (value !== this.value) {
        this.value = value;
        this.__notifier.next(value);
      }
    });
  }
}
import { merge } from 'rxjs';
import type { Epic } from './epic';

/**
  Merges all epics into a single one.
 */
export function combineEpics<
  Input = unknown,
  Output extends Input = Input,
  State = void,
  Dependencies = any
>(
  ...epics: Epic<Input, Output, State, Dependencies>[]
): Epic<Input, Output, State, Dependencies> {
  const merger: Epic<Input, Output, State, Dependencies> = (...args) =>
    merge(
      ...epics.map((epic) => {
        const output$ = epic(...args);
        if (!output$) {
          throw new TypeError(
            `combineEpics: one of the provided Epics "${
              epic.name || '<anonymous>'
            }" does not return a stream. Double check you're not missing a return statement!`
          );
        }
        return output$;
      })
    );

  // Technically the `name` property on Function's are supposed to be read-only.
  // While some JS runtimes allow it anyway (so this is useful in debugging)
  // some actually throw an exception when you attempt to do so.
  try {
    Object.defineProperty(merger, 'name', {
      value: `combineEpics(${epics
        .map((epic) => epic.name || '<anonymous>')
        .join(', ')})`,
    });
  } catch (e) {
    // noop
  }

  return merger;
}
import type { Dispatch, Middleware, MiddlewareAPI } from 'redux';
import { Subject, from, queueScheduler } from 'rxjs';
import { map, mergeMap, observeOn, subscribeOn } from 'rxjs/operators';
import { StateObservable } from './StateObservable';
import type { Epic } from './epic';
import { warn } from './utils/console';

interface Options<D = any> {
  dependencies?: D;
}

export interface EpicMiddleware<
  Input = unknown,
  Output extends Input = Input,
  State = void,
  Dependencies = any
  // eslint-disable-next-line @typescript-eslint/ban-types
> extends Middleware<{}, State> {
  run(rootEpic: Epic<Input, Output, State, Dependencies>): void;
}

export function createEpicMiddleware<
  Input = unknown,
  Output extends Input = Input,
  State = void,
  Dependencies = any
>(
  options: Options<Dependencies> = {}
): EpicMiddleware<Input, Output, State, Dependencies> {
  // This isn't great. RxJS doesn't publicly export the constructor for
  // QueueScheduler nor QueueAction, so we reach in. We need to do this because
  // we don't want our internal queuing mechanism to be on the same queue as any
  // other RxJS code outside of redux-observable internals.
  const QueueScheduler: any = queueScheduler.constructor;
  // eslint-disable-next-line @typescript-eslint/no-unsafe-assignment, @typescript-eslint/no-unsafe-call
  const uniqueQueueScheduler: typeof queueScheduler = new QueueScheduler(
    // eslint-disable-next-line @typescript-eslint/no-unsafe-member-access
    (queueScheduler as any).schedulerActionCtor
  );

  if (process.env.NODE_ENV !== 'production' && typeof options === 'function') {
    throw new TypeError(
      'Providing your root Epic to `createEpicMiddleware(rootEpic)` is no longer supported, instead use `epicMiddleware.run(rootEpic)`\n\nLearn more: https://redux-observable.js.org/MIGRATION.html#setting-up-the-middleware'
    );
  }

  const epic$ = new Subject<Epic<Input, Output, State, Dependencies>>();
  let store: MiddlewareAPI<Dispatch<any>, State>;

  const epicMiddleware: EpicMiddleware<Input, Output, State, Dependencies> = (
    _store
  ) => {
    if (process.env.NODE_ENV !== 'production' && store) {
      // https://github.com/redux-observable/redux-observable/issues/389
      warn(
        'this middleware is already associated with a store. createEpicMiddleware should be called for every store.\n\nLearn more: https://goo.gl/2GQ7Da'
      );
    }
    store = _store;
    const actionSubject$ = new Subject<Input>();
    const stateSubject$ = new Subject<State>();
    const action$ = actionSubject$
      .asObservable()
      .pipe(observeOn(uniqueQueueScheduler));
    const state$ = new StateObservable(
      stateSubject$.pipe(observeOn(uniqueQueueScheduler)),
      store.getState()
    );

    const result$ = epic$.pipe(
      map((epic) => {
        const output$ = epic(action$, state$, options.dependencies!);

        if (!output$) {
          throw new TypeError(
            `Your root Epic "${
              epic.name || '<anonymous>'
            }" does not return a stream. Double check you're not missing a return statement!`
          );
        }

        return output$;
      }),
      mergeMap((output$) =>
        from(output$).pipe(
          subscribeOn(uniqueQueueScheduler),
          observeOn(uniqueQueueScheduler)
        )
      )
    );

    result$.subscribe(store.dispatch);

    return (next) => {
      return (action) => {
        // Downstream middleware gets the action first,
        // which includes their reducers, so state is
        // updated before epics receive the action
        const result = next(action);

        // It's important to update the state$ before we emit
        // the action because otherwise it would be stale
        stateSubject$.next(store.getState());
        actionSubject$.next(action as Input);

        return result;
      };
    };
  };

  epicMiddleware.run = (rootEpic) => {
    if (process.env.NODE_ENV !== 'production' && !store) {
      warn(
        'epicMiddleware.run(rootEpic) called before the middleware has been setup by redux. Provide the epicMiddleware instance to createStore() first.'
      );
    }
    epic$.next(rootEpic);
  };

  return epicMiddleware;
}
let deprecationsSeen: { [key: string]: boolean } = {};
export const resetDeprecationsSeen = (): void => {
  deprecationsSeen = {};
};

const consoleWarn =
  typeof console === 'object' && typeof console.warn === 'function'
    ? console.warn.bind(console)
    : () => {};

export const deprecate = (msg: string): void => {
  if (!deprecationsSeen[msg]) {
    deprecationsSeen[msg] = true;
    consoleWarn(`redux-observable | DEPRECATION: ${msg}`);
  }
};

export const warn = (msg: string): void => {
  consoleWarn(`redux-observable | WARNING: ${msg}`);
};
import { isAction, type Action } from 'redux';
import type { OperatorFunction } from 'rxjs';
import { filter } from 'rxjs/operators';
import { warn } from './utils/console';

/**
 * Inferring the types of this is a bit challenging, and only works in newer
 * versions of TypeScript.
 *
 * @param ...types One or more Redux action types you want to filter for, variadic.
 */
export function ofType<
  // All possible actions your app can dispatch
  Input,
  // The types you want to filter for
  Type extends string,
  // The resulting actions that match the above types
  Output extends Input = Extract<Input, Action<Type>>
>(...types: [Type, ...Type[]]): OperatorFunction<Input, Output> {
  const len = types.length;

  if (process.env.NODE_ENV !== 'production') {
    if (len === 0) {
      warn('ofType was called without any types!');
    }
    if (types.some((key) => key === null || key === undefined)) {
      warn('ofType was called with one or more undefined or null values!');
    }
  }

  return filter(
    len === 1
      ? (action): action is Output => isAction(action) && action.type === types[0]
      : (action): action is Output => {
        if (isAction(action)) {
          for (let i = 0; i < len; i++) {
            if (action.type === types[i]) {
              return true;
            }
          }
        }

        return false;
      }
  );
}
